CREATE DATABASE  IF NOT EXISTS `quiz_patente` ;
USE `quiz_patente`;

DROP TABLE IF EXISTS `utente`;
CREATE TABLE `utente` (
  `NomeUtente` varchar(50) NOT NULL,
  PRIMARY KEY (`NomeUtente`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

LOCK TABLES `utente` WRITE;
INSERT INTO `utente` VALUES ('Alice'),('Gabriele'),('Rita'),('User');
UNLOCK TABLES;

DROP TABLE IF EXISTS `svolge`;
CREATE TABLE `svolge` (
  `Utente` varchar(50) NOT NULL,
  `Scheda` int(11) NOT NULL,
  `Minuti` decimal(2,0) DEFAULT NULL,
  `Errori` int(11) DEFAULT NULL,
  `DataSvolgimento` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`Utente`,`Scheda`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

LOCK TABLES `svolge` WRITE;
INSERT INTO `svolge` VALUES ('alice',0,13,5,'2021-06-06 10:04:36'),('alice',1,4,3,'2021-06-06 10:09:21');
UNLOCK TABLES;

DROP TABLE IF EXISTS `scheda`;
CREATE TABLE `scheda` (
  `CodiceScheda` int(11) NOT NULL,
  `Difficolta` varchar(50) NOT NULL,
  PRIMARY KEY (`CodiceScheda`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

LOCK TABLES `scheda` WRITE;
INSERT INTO `scheda` VALUES (0,'Facile'),(1,'Media'),(2,'Difficile');
UNLOCK TABLES;

DROP TABLE IF EXISTS `domanda`;
CREATE TABLE `domanda` (
  `CodiceDomanda` int(11) NOT NULL,
  `Testo` blob NOT NULL,
  `Risposta` int(11) NOT NULL,
  `ImmagineURL` varchar(150) DEFAULT NULL,
  `Scheda` int(11) NOT NULL,
  PRIMARY KEY (`CodiceDomanda`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

